using Gardener.Enums;
using Gardener.LocalizationLocalizer;
using Gardener.VerifyCode.Dtos;
using Gardener.VerifyCode.Services;
using Microsoft.AspNetCore.Components;

namespace Gardener.Client.AntDesignUi.Base.Components
{
    public partial class ImageVerifyCode
    {
        [Inject]
        private IImageVerifyCodeService verifyCodeService { get; set; } = null!;  
        [Inject]
        private ILocalizationLocalizer localizer { get; set; } = null!;
        private string verifyCodeImage = "data:image/gif;base64,";

        [Parameter]
        public string Style { get; set; } = "height:38px;margin-left:2px;";
        /// <summary>
        /// 字符集类型
        /// </summary>
        [Parameter]
        public CodeCharacterTypeEnum? Type { get; set; }
        /// <summary>
        /// 校验码字符数量
        /// </summary>
        [Parameter]
        public int? CharacterCount { get; set; }
        /// <summary>
        /// 字符大小
        /// </summary>
        [Parameter]
        public int? FontSize { get; set; }
        [Parameter]
        public string? VerifyCodeKey { get; set; }
        [Parameter]
        public EventCallback<string> VerifyCodeKeyChanged { get; set; }

        private bool isLoading = false;

        private ImageVerifyCodeOutput? codeOutput;
        protected override async Task OnInitializedAsync()
        {
            await ReLoadVerifyCode();
        }
        /// <summary>
        /// 
        /// </summary>
        /// <returns></returns>
        public async Task ReLoadVerifyCode()
        {
            isLoading = true;
            Task? removeTask=null;
            //移除上次的验证码
            if (!string.IsNullOrEmpty(VerifyCodeKey))
            {
                removeTask = verifyCodeService.Remove(VerifyCodeKey);
            }
            ImageVerifyCodeInput input = new ImageVerifyCodeInput()
            {
                FontSize = FontSize,
                CreateCodeParam = new CharacterCodeCreateParam
                {
                    Type = Type,
                    CharacterCount = CharacterCount
                }
            };
            codeOutput = await verifyCodeService.Create(input);
            if (codeOutput != null)
            {
                verifyCodeImage = "data:image/gif;base64," + codeOutput.Base64Image;
                VerifyCodeKey = codeOutput.Key;
                await VerifyCodeKeyChanged.InvokeAsync(VerifyCodeKey);
            }
            if (removeTask != null)
            { 
                await removeTask; 
            }
            isLoading = false;
        }

        public ImageVerifyCodeOutput? GetCodeOutput()
        {
            return codeOutput;
        }
    }
}
